set-variable.value:
#[%dw 2.0
			output application/json
			---
			{
			    "prompt": "What is the capital of Germany? Provide only the answer in JSON format with country and city for both"
			}]

ms-inference:chat-answer-prompt.ms-inference:chat-request-attributes:
{
			  "response_format": {
				"type": "json_schema",
				"json_schema": {
				  "name": "location_schema",
				  "schema": {
					"type": "object",
					"properties": {
					  "countryName": {
						"description": "The name of the country.",
						"type": "string"
					  },
					  "cityName": {
						"description": "The name of the city.",
						"type": "string"
					  }
					},
					"required": [
					  "countryName",
					  "cityName"
					]
				  }
				}
			  }
			}

set-variable.value:
#[%dw 2.0
			output application/json
			---
			[{
			  "role": "assistant",
			  "content": "You are a helpful assistant."
			},
			{
			  "role": "user",
			  "content": "What is the capital of Switzerland! Provide only the answer in JSON format with country and city"
			}
			]]

